// Function to get the offsets based on the range `r`
inline const std::vector<std::tuple<int, int, int>> &getOffsetsForR(int r)
{
    if (r == 0)
        return OFFSETS_R0;
    if (r == 1)
        return OFFSETS_R1;
    if (r == 2)
        return OFFSETS_R2;
    if (r == 3)
        return OFFSETS_R3;
    if (r == 4)
        return OFFSETS_R4;
    if (r == 5)
        return OFFSETS_R5;
    if (r == 6)
        return OFFSETS_R6;
    return OFFSETS_R2; // Default to r = 2
}

